import moment from 'moment'; //处理时间戳

export default {
  data: function data() {
    return {
      isLogin: false,
      //判断是否登录
      modifyFlag: "",
      //判断是否出现 “编辑”
      blogId: "",
      //博客id
      user: "",
      //存储博客用户
      blog: "",
      //存储博客
      userId: localStorage.getItem("login"),
      //系统当前用户
      message: "",
      //存储弹出的点赞消息
      loginUser: {},
      //登录用户
      firstFloor: {},
      //一楼评论列表
      content: "",
      contentSecond: "",
      //输入的评论内容
      replyId: "",
      //点击“回复”，传过来的评论id
      storeclass: "",
      //输入框的分类
      //inputResult:"",		//新建分类的返回结果
      storeList: {},
      //总的收藏分类对象列表
      storename: "",
      //当前点击收藏分类
      scId: "",
      //收藏分类表id
      usersmark: {},
      //排名	等级
      unread: {},
      //未读的关注消息列表
      // inputResultNull:false,		//未查到
      inputTitle: "",
      //要查询的博客标题
      newSixBloglist: {},
      //最新的6个博客
      classifyList: {},
      //博客分类列表
      sixHotBlog: {} //阅读数最多的6个博客

    };
  },
  mounted: function mounted() {
    //判断是否要出现“我的，消息”链接
    if (localStorage.getItem("login") != null) {
      this.isLogin = true;
    } //获取index首页传过来的博客id


    this.blogId = this.$router.history.current.params.id; // console.log("blogid---------"+this.blogId);
    //获取这个博客所属的用户

    this.getUserBybid(); //访问量+1

    this.addVisit(); //获取登录用户

    this.getLoginUser(); //获取一楼评论列表

    this.getFirstFloor(); //查找未读的关注消息列表

    this.getUnread();
  },
  filters: {
    formatDate: function formatDate(value) {
      // console.log("====================");
      // console.log(moment(value).valueOf());
      return moment(value.time).format('YYYY-MM-DD HH:mm:ss');
    } // formatDateyy: function (value) {
    // 	// console.log("====================");
    // 	// console.log(moment(value).valueOf());
    // 	return moment(value.time).format('YYYY-MM-DD');
    // }

  },
  methods: {
    //6个最火（点击量多）blog
    getHotBlogs: function getHotBlogs() {
      var _this = this;

      var data = {
        userId: this.user.id
      };
      this.$ajax.post('/api/blog/sixHotBlogs', this.$qs.stringify(data)).then(function (response) {
        _this.sixHotBlog = response.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    //博客分类
    getClassifylist: function getClassifylist() {
      var _this2 = this;

      var data = {
        userId: this.user.id
      };
      this.$ajax.post('/api/classify/classnameList', this.$qs.stringify(data)).then(function (response) {
        _this2.classifyList = response.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    //最新的6个博客
    getSixBlogs: function getSixBlogs() {
      var _this3 = this;

      var data = {
        userId: this.user.id
      };
      this.$ajax.post('/api/blog/sixNewList', this.$qs.stringify(data)).then(function (response) {
        _this3.newSixBloglist = response.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    //模糊查询博客
    likeTitle: function likeTitle() {
      if (!this.inputTitle) {
        return;
      }

      this.$router.push("/selectBlog".concat(this.inputTitle));
    },
    //查找未读的关注消息列表
    getUnread: function getUnread() {
      var _this4 = this;

      if (!this.isLogin) {
        //没有登录就不运行“获取未读消息”这个方法
        return;
      } else {
        var data = {
          receiveUserId: this.userId
        };
        this.$ajax.post('/api/notice/messagelist', this.$qs.stringify(data)).then(function (response) {
          _this4.unread = response.data;
        }).catch(function (error) {
          console.log(error);
        });
      }
    },
    //排名总运算
    getFloor: function getFloor() {
      var _this5 = this;

      var data = {
        uid: this.user.id
      };
      this.$ajax.post('/api/user/computeFloor', this.$qs.stringify(data)).then(function (response) {
        console.log(response.data);
        _this5.usersmark = response.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    //评论举报
    forbidReply: function forbidReply(rid) {
      //判断登录没，没登陆不给举报
      if (!this.isLogin) {
        this.$router.push("/login");
      } else {
        var data = {
          rid: rid
        };
        this.$ajax.post('/api/reply/forbidReply', this.$qs.stringify(data)).then(function (response) {
          alert("举报成功");
        }).catch(function (error) {
          console.log(error);
        });
      }
    },
    //评论时，先判断是否登录
    inputCheck: function inputCheck() {
      //没登陆转到登录页
      if (!this.isLogin) {
        this.$router.push("/login");
      }
    },
    //查看博客
    gotoBlogDetail: function gotoBlogDetail(bid) {
      this.$router.push("/blogDetail".concat(bid));
    },
    //去博客中心
    gotoBlogCenter: function gotoBlogCenter(uid) {
      // alert(uid);
      this.$router.push("/myBlogCenter".concat(uid));
    },
    //举报
    forbid: function forbid() {
      //用户没登陆，直接跳转到 登录页
      if (!this.isLogin) {
        this.$router.push("/login");
      } else {
        var data = {
          id: this.blog.id
        };
        this.$ajax.post('/api/blog/forbidBlog', this.$qs.stringify(data)).then(function (response) {
          alert("举报成功");
        }).catch(function (error) {
          console.log(error);
        });
      }
    },
    //新增收藏记录
    sellStore: function sellStore() {
      if (!this.storename) {
        alert("请选择分类");
        return;
      }

      if (this.storename == "请选择") {
        alert("请选择分类");
        return;
      }

      var data = {
        scId: this.scId,
        userId: this.userId,
        blogId: this.blog.id
      };
      this.$ajax.post('/api/store/addStore', this.$qs.stringify(data)).then(function (response) {
        alert(response.data);
        $('#myStore').modal('hide');
      }).catch(function (error) {
        console.log(error);
      });
    },
    //获取当前点击的收藏分类名
    getStorename: function getStorename(event) {
      var _this6 = this;

      this.getStoreList();
      this.storename = event.target.value;

      if (this.storename == "请选择") {
        alert("请选择分类");
        return;
      }

      console.log("点击的分类名" + this.storename); //获取分类民classname

      var data = {
        uid: this.userId,
        classname: this.storename
      };
      console.log(data);
      this.$ajax.post('/api/storeclass/getScId', this.$qs.stringify(data)).then(function (response) {
        _this6.scId = response.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    //获取所有收藏分类
    getStoreList: function getStoreList() {
      var _this7 = this;

      var data = {
        uid: this.userId
      };
      this.$ajax.post('/api/storeclass/getList', this.$qs.stringify(data)).then(function (response) {
        _this7.storeList = response.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    //新增收藏分类
    addStoreclass: function addStoreclass() {
      var _this8 = this;

      //处理用户输入的新建分类为空
      if (!this.storeclass) {
        return;
      }

      var data = {
        uid: this.userId,
        classname: this.storeclass
      };
      this.$ajax.post('/api/storeclass/addStoreclass', this.$qs.stringify(data)).then(function (response) {
        alert(response.data);
        _this8.storeclass = "";
      }).catch(function (error) {
        console.log(error);
      });
    },
    //展示收藏分类
    showStoreclass: function showStoreclass() {
      //没登陆的处理
      if (!this.isLogin) {
        this.$router.push("/login");
      } else {
        $('#myStore').modal('show');
        this.getStoreList();
      }
    },
    //添加“关注”消息记录
    setAttentionRecord: function setAttentionRecord() {
      var data = {
        sendUserId: localStorage.getItem("login"),
        receiveUserId: this.user.id,
        type: "关注"
      };
      this.$ajax.post('/api/notice/attentionRecord', this.$qs.stringify(data)).then(function (response) {
        console.log("关注记录");
      }).catch(function (error) {
        console.log(error);
      });
    },
    //关注
    attention: function attention() {
      var _this9 = this;

      //判断登录，没登录就去登录
      if (!this.isLogin) {
        this.$router.push("/login");
      } else {
        var data = {
          userId: localStorage.getItem("login"),
          byUserId: this.user.id
        };
        this.$ajax.post('/api/attention/addAttention', this.$qs.stringify(data)).then(function (response) {
          // 要判断有没有关注，关注的话，往通知表里加 “关注” 记录
          if (response.data == "成功关注") {
            //添加"关注"消息记录
            _this9.setAttentionRecord(); // alert(response.data);

          }

          alert(response.data); // this.$router.go(0);
        }).catch(function (error) {
          console.log(error);
        });
      }
    },
    //删除评论
    deleteReply: function deleteReply(rid, length) {
      var _this10 = this;

      var data = {
        id: rid,
        size: length
      };
      console.log(data);
      this.$ajax.post('/api/reply/deleteReplyAndChild', this.$qs.stringify(data)).then(function (response) {
        alert("删除成功");

        _this10.$router.go(0);
      }).catch(function (error) {
        console.log(error);
      });
    },
    //是否显示查看回复
    showReply: function showReply(twolist) {
      if (twolist.size() == 0) {
        return false;
      } else {
        return true;
      }
    },
    //查看回复
    showsecond: function showsecond(id) {
      if ($("#".concat(id)).css("display") === "none") {
        $("#".concat(id)).css("display", "block"); // $(`.${id}`).text("收起回复");
      } else {
        $("#".concat(id)).css("display", "none"); // $(`.${id}`).text("查看回复");
      }
    },
    //点击 “回复” 显示模态框
    showReplyModel: function showReplyModel(id) {
      //判断登录，没登陆直接打道回府
      if (!this.isLogin) {
        this.$router.push("/login");
      } else {
        $('#myModal').modal('show'); //模态框显示

        this.replyId = id;
      } // console.log("---------------");
      // console.log(this.replyId);
      // this.addReplyInfo(id);

    },
    //二次评论
    addReplyInfo: function addReplyInfo() {
      var _this11 = this;

      if (!this.contentSecond) {
        alert("评论内容不能为空");
        return;
      }

      var data = {
        parentId: this.replyId,
        userId: this.userId,
        blogId: this.blogId,
        content: this.contentSecond
      };
      this.$ajax.post('/api/reply/addReply', this.$qs.stringify(data)).then(function (response) {
        // alert("评论成功");
        _this11.contentSecond = "";
        $('#myModal').modal('hide'); //模态框隐藏
        //添加评论消息记录

        _this11.addComRecord();

        _this11.$router.go(0);
      }).catch(function (error) {
        console.log(error);
      });
    },
    //获取一楼评论列表
    getFirstFloor: function getFirstFloor() {
      var _this12 = this;

      var data = {
        blogId: this.blogId
      };
      this.$ajax.post("/api/reply/getFirstFloor", this.$qs.stringify(data)).then(function (response) {
        _this12.firstFloor = response.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    //添加评论消息记录
    addComRecord: function addComRecord() {
      var data = {
        sendUserId: this.userId,
        receiveUserId: this.blog.userId,
        type: "评论",
        targetId: this.blog.id
      };
      this.$ajax.post('/api/notice/attentionRecord', this.$qs.stringify(data)).then(function (response) {}).catch(function (error) {
        console.log(error);
      });
    },
    //添加一级评论
    addReply: function addReply() {
      var _this13 = this;

      //检查评论内容不容为空
      if (!this.content) {
        alert("评论内容不能为空");
        return;
      }

      var data = {
        userId: this.userId,
        blogId: this.blogId,
        content: this.content
      };
      this.$ajax.post('/api/reply/addReply', this.$qs.stringify(data)).then(function (response) {
        // alert("评论成功");
        //添加评论消息记录
        _this13.addComRecord(); // this.content = " ";


        _this13.$router.go(0);
      }).catch(function (error) {
        console.log(error);
      });
    },
    //获取登录用户
    getLoginUser: function getLoginUser() {
      var _this14 = this;

      if (!this.isLogin) {
        return;
      } else {
        var data = {
          id: this.userId
        };
        this.$ajax.post('/api/user/getUserById', this.$qs.stringify(data)).then(function (response) {
          _this14.loginUser = response.data;
        }).catch(function (error) {
          console.log(error);
        });
      }
    },
    //退出登录
    loginOut: function loginOut() {
      localStorage.removeItem("login"); // this.$router.push("/");		这个会跳转当前页面，但是不会刷新

      this.$router.go(0); //重新刷新当前页面
    },
    //添加点赞消息记录
    addAppreRecord: function addAppreRecord() {
      var data = {
        sendUserId: this.userId,
        //登录用户
        receiveUserId: this.blog.userId,
        //博客所属用户
        type: "点赞",
        targetId: this.blog.id //被点赞博客id

      };
      this.$ajax.post('/api/notice/attentionRecord', this.$qs.stringify(data)).then(function (response) {// console.log()
      }).catch(function (error) {
        console.log(error);
      });
    },
    //点赞
    addAppreciate: function addAppreciate() {
      var _this15 = this;

      //判断是否登录
      if (!this.isLogin) {
        this.$router.push("/login");
      } else {
        //判断是否点赞过
        var data = {
          uid: this.blog.userId,
          //被点赞人
          blogId: this.blog.id,
          userId: localStorage.getItem("login")
        };
        console.log(data);
        this.$ajax.post('/api/appreciate/addAppreciate', this.$qs.stringify(data)).then(function (response) {
          if (response.data == "点赞成功") {
            //添加点赞消息记录
            _this15.addAppreRecord();
          }

          _this15.message = response.data;
          alert(_this15.message);

          _this15.$router.go(0);
        }).catch(function (error) {
          console.log(error);
        });
      }
    },
    //利用blogid	获取用户
    getUserBybid: function getUserBybid() {
      var _this16 = this;

      // console.log("----------getUserBybId------------");
      var data = {
        id: this.blogId
      };
      this.$ajax.post("/api/user/queryUserByBlogid", this.$qs.stringify(data)).then(function (response) {
        // console.log("用户--------------"+response.data);
        _this16.user = response.data; // console.log(this.user.id);
        //判断当前用户是不是博主，不是的话，用户没有权限修改其他博主的博客

        if (_this16.user.id == _this16.userId) {
          _this16.modifyFlag = true;
        } else {
          _this16.modifyFlag = false;
        }

        console.log("=====要加在这里啊，不然获取不到所属博客用户========================"); //排名总运算

        _this16.getFloor(); //获取博客


        _this16.getBlog(); //最新


        _this16.getSixBlogs(); //最火


        _this16.getHotBlogs(); //分类


        _this16.getClassifylist();
      }).catch(function (error) {
        console.log(error);
      });
    },
    getBlog: function getBlog() {
      var _this17 = this;

      //根据bid查blog和username
      var data = {
        id: this.blogId
      };
      this.$ajax.post("/api/blog/queryBlogById", this.$qs.stringify(data)).then(function (response) {
        _this17.blog = response.data;
      }).catch(function (error) {
        console.log(error);
      });
    },
    //访问量+1
    addVisit: function addVisit() {
      var data = {
        id: this.blogId
      };
      this.$ajax.post('/api/blog/addVisit', this.$qs.stringify(data)).then(function (response) {// console.log(response.data);
      }).catch(function (error) {
        console.log(error);
      });
    }
  }
};
