result:
/* 选择分类 */
#selectStoreclass{
	width:50%;
	margin-left: 20px;
	display: inline;
	float: left;
	margin-left: 15px;
}
/* 收藏分类 */
#storeclass{
	width: 80%;
	float: left;
}
/* 面板 */
.D-card-left{
	/* position: absolute; */
	float: left;
	margin-left: 120px;
	margin-top: -10px;
	width: 21.5%;
	background-color: #F8F8F8;

}

.D-card-left>div{
	/* height: 200px; */
	padding-bottom: 10px;
	padding-top: 5px;
	margin-bottom: 10px;
	/* margin-top: 10px; */
	box-shadow: 0px 2px 4px 0 rgb(0,0,0,0.05); 
	/* border: 1px solid lightgray; */
	background-color: white;
}

.card-left-user-ava{
	height: 80px;
	padding: 20px 16px 12px 16px;
	width: 100%;
}
.card-left-user-ava-{
	height: 48px;
	width: 48px;
	position: relative;
	float: left;
}
.card-left-user-ava->img{
	height: 48px;
	width: 48px;
	border-radius: 50%;
}
.card-left-user-ava-detail{
	float: left;
	margin-left: 8px;
	/* background-color: darkgray; */
	position: relative;
}
.username{
	line-height: 20px;
	/* font-size: 20px; */
	height: 20px;
	text-align: left;
}
.gohouse{
	padding-top: 10px;
	color: #3399EA;
}
 /* 原创粉丝。。。 */
.five-parts{
	padding: 6px 10px;
	height: 45px;
	margin-bottom: 15px;
}
.part{
	position: relative;
	float: left;
	width: 56px;
	/* line-height: 44px; */
	text-align: center;
}
.part>div{
	margin-bottom: 4px;
}
.part>dt{
	display: inline-block;
}
/* 等级 */
.sort-parts{
	margin-top: -8px;
	margin-left: 5px;
	margin-bottom: 5px;
	padding: 16px;
	text-align: left;
}

.sort-parts>div{
	float: left;
	position: relative;
	/* text-align: center; */
	width: 50%;
	/* margin-right: 10px; */
}
.part-last{
	margin-bottom: 20px;
}
.other-more{	/* 关注/私信 */
	height: 50px;
	border-top: 0.1px solid #E8E8E8;
	/* margin-top: 20px; */
	padding: 16px 16px 0 16px;
	text-align: left;
	/* padding-bottom: -5px; */
}
.other-more>button{
	height: 28px;
	font-size: 12px;
	line-height: 28px;
	width: 48%;
	border-radius: 5px;

}
.other-more-see{
	background-color: #CD0000;
	color: white;
	border: none;
}
.other-more-see:hover{
	background-color: #B22222;
}
.other-more-msg{
	border: 1px solid #CD0000;
	color: #CD0000;
	background-color: white;
	float: right;
}
.other-more-msg:hover{
	background-color: #FFAEB9;
}



/* 最新文章 */
.blog-last{
	/* height: 200px; */
	background-color: navajowhite;
	/* padding: 5px 0; */
}
.blog-fam{
	font-size: 15px;
	/* margin-left: 5px; */
	/* margin-top: 5px; */
	color: #2c3030;
	border-bottom: 0px;
}
.blog-fam>i{
	color: #CD0000;/* 红色 */
}
.zon-title{
	padding: 5px 16px 0px 16px;
	text-align: left;
}
.zon-title>div{
	margin-bottom: 14px;
	overflow: hidden;
	text-overflow: ellipsis;
	word-break:keep-all;/* (不换行） */
	white-space:nowrap;/* (不换行） */
}

/* 博客分类 */
.blog-sort{
	padding-top: 5px;
}
.blog-sort-each{
	padding: 0 16px;
	margin-top: 3px;
	text-align: left;
}
.blog-sort-each>p{
	height: 32px;
	line-height: 32px;
	margin-bottom: 16px;
}
.sort-each-img{
	height: 32px;
	width: 32px;
	margin-right: 16px;
	/* float: left; */
}
.sort-each-name{
	width: auto;
	line-height: 32px;
}
.sort-each-num{
	float: right;
	right: 0;
	color: #858585;
}
/* 时间分类 */
.blog-sort-month-each{
	padding:0 16px;
	line-height: 22px;
	text-align: left;
}
.blog-sort-month-each>p{
	margin-bottom: 8px;
}
/* 热门 */
.blog-sort-hot{
	padding:0 16px;
	text-align: left;
}
.blog-sort-hot>div{
	margin-bottom: 10px;
}
.hot-title{
	line-height: 22px;
	margin-bottom: 0px;
	overflow: hidden;
	text-overflow: ellipsis;
	word-break:keep-all;/* (不换行） */
	white-space:nowrap;/* (不换行） */
}
.hot-num{
	color: #858585;
	font-size: 12px;
	line-height: 20px;
}


/* 右边-博客 */
.D-card-right{
	/* position: absolute; */
	float: right;
	margin-right: 140px;
	margin-top: -10px;
	width: 59.5%;
	background-color: white;
	/* padding: 0 24px 16px 0; */
	/* background-color: antiquewhite; */
}
.D-card-right>div{
	background-color: white;

}	
/* 博客标题 */
.blog-title{
	padding: 0 24px;
	font-size: 26px;
	font-weight: 600;
	line-height: 44px;
	text-align: left;
	padding-top: 16px;
	margin-bottom: 8px;
	border-bottom: none;
	color: #000000;
}
.blog-title-user{
	padding: 0 24px;
	padding-bottom: 14px;
	line-height: 26px;
	text-align: left;
	color: #858585;
	margin-right: 20px;
	border-bottom: 1px solid #F5F5F5;
}
.blog-title-user>span{
	margin-right: 20px;
}
.blog-title-user>span>a{
	color: #78A5F1;
}
.blog-take{
	color: #CD0000;
}
.blog-modify{
	color: #3399EA;
}
/* 博客具体内容 */
article{
	margin-top: 16px;
	margin: 0 24px;
	padding-top: 16px;
	/* height:400px; */
	text-align: left;
}
/* 点赞		收藏		举报 */
.blogD-bottom{
	padding-top: 24px;
	padding-bottom: 16px;
	display: flex;
	margin-left: 20px;
}
.blogD-bottom>span{
	padding: 6px 8px;
	background: #f5f6f7;
	font-size: 14px;
	font-weight: 500;
	margin-right: 8px;
	padding: 7px 10px;
}
.blogD-bottom>span>a>span{
	margin-right: 8px;
}
/* 评论 */
.com-bottom{
	margin-top: 1px;
	    background: #fff;
		    box-shadow: 0 2px 4px 0 rgba(0,0,0,0.05);
		    margin-bottom: 8px;
		    padding-bottom: 8px;
}
.com-input{
	padding: 16px 24px 8px;
}
.com-user{
	    margin-right: 8px;
		float: left;
}
.com-user>a>img{
	    margin-top: 8px;
	    display: block;
	    width: 25px;
	    height: 25px;
	    border-radius: 50%;
	    border: 1px solid #e0e0e0;
}
.com-textarea{
	    display: block;
	    width: 94%;
		/* float: right; */
	    padding: 7px 8px;
	    background: #fff;
	    border: 1px solid #c1c1c1;
	    border-radius: 4px;
	    resize: none;
	    height: 60px;
	    font-size: 14px;
	    line-height: 22px;
		ox-sizing: border-box;
		transition: height .3s ease-in-out;	
}
.com-btn{
	margin-top: 10px;
	float: right;
	display: block;
}
.com-list{
	/* padding: 0 24px; */
	    overflow: hidden;
}
.com-list>ul{
	margin-top: 16px;
	margin-left: -20px;
	    border-bottom: 1px dashed #e0e0e0;
}
.list-0{
	    position: relative;
	    margin-bottom: 12px;
		list-style: none;
}
.list-0>a>img{
	    display: block;
	    margin-right: 8px;
	    width: 24px;
	    height: 24px;
	    border-radius: 50%;
		float: left;
}
.com-right{
	position: relative;
	    width: 97%;

		text-align: left;
		line-height: 22px;
}
.com-right>span{
	margin-left: 16px;
}
/* 时间 */
.com-right-time{
	font-size: 12px;
	    color: #999;
	    vertical-align: top;
	    display: inline-block;

}
/* 操作 */
.com-cao{
	vertical-align: top;
	    display: inline-block;
	    position: absolute;
	    top: 0;
	    right: 40px;

}
.com-cao>a{
	    /* display: none; */
	    margin-right: 10px;
	    min-width: auto;
	    font-size: 12px;
	    height: 20px;
	    line-height: 20px;
		color: #79a5e5 !important;
}
.com-reply{
	display: none;
	margin-left: 32px;
	    padding-left: 8px;
	    border-left: 4px solid #c5c5c5;
		list-style: none;
}
.com-reply>ul>img{
	display: block;
	    margin-right: 8px;
	    width: 24px;
	    height: 24px;
	    border-radius: 50%;
}
a:hover{
	color: #CD0000;
}
